# qt_styles.py\nAPP_QSS = \"\"\"\n/* Base */\nQWidget {\n    color: #E6E6E6;\n    font-family: \"Segoe UI\";\n    font-size: 12px;\n}\n\n/* Main card background */\n#MainCard {\n    background-color: #1B232A;\n    border: 1px solid #2C3A45;\n    border-radius: 12px;\n}\n\n/* Header */\n#HeaderBar {\n    background-color: rgba(0,0,0,0);\n}\n\n#TitleLabel {\n    font-size: 14px;\n    font-weight: 800;\n    color: #EAEAEA;\n}\n\nQToolButton {\n    border: 0px;\n    border-radius: 8px;\n    padding: 6px 8px;\n    background: transparent;\n    color: #CFCFCF;\n}\nQToolButton:hover {\n    background: rgba(255,255,255,0.08);\n}\nQToolButton:pressed {\n    background: rgba(255,255,255,0.12);\n}\n\nQToolButton#CloseBtn:hover {\n    background: rgba(255, 72, 72, 0.25);\n    color: #FFD0D0;\n}\n\n/* Scroll area */ \nQScrollArea {\n    border: 0px;\n    background: transparent;\n}\n\nQScrollBar:vertical {\n    background: transparent;\n    width: 16px;\n    margin: 6px 2px 6px 2px;\n}\n\nQScrollBar::add-page:vertical,\nQScrollBar::sub-page:vertical {\n    background: none;\n}\n\n/* 3 csík (grip) a scrollbar csúszkán */\nQScrollBar::handle:vertical {\n    border-radius: 6px;\n    min-height: 30px;\n    background: qlineargradient(x1:0, y1:0, x2:0, y2:1,\n        stop: 0 rgba(255,255,255,0.12),\n        stop: 0.42 rgba(255,255,255,0.12),\n        stop: 0.421 rgba(255,255,255,0.5),\n        stop: 0.44 rgba(255,255,255,0.5),\n        stop: 0.441 rgba(255,255,255,0.12),\n        stop: 0.49 rgba(255,255,255,0.12),\n        stop: 0.491 rgba(255,255,255,0.5),\n        stop: 0.51 rgba(255,255,255,0.5),\n        stop: 0.511 rgba(255,255,255,0.12),\n        stop: 0.56 rgba(255,255,255,0.12),\n        stop: 0.561 rgba(255,255,255,0.5),\n        stop: 0.58 rgba(255,255,255,0.5),\n        stop: 0.581 rgba(255,255,255,0.12),\n        stop: 1 rgba(255,255,255,0.12)\n    );\n}\n\nQScrollBar::handle:vertical:hover {\n    background: qlineargradient(x1:0, y1:0, x2:0, y2:1,\n        stop: 0 rgba(255,255,255,0.18),\n        stop: 0.42 rgba(255,255,255,0.18),\n        stop: 0.421 rgba(255,255,255,0.8),\n        stop: 0.44 rgba(255,255,255,0.8),\n        stop: 0.441 rgba(255,255,255,0.18),\n        stop: 0.49 rgba(255,255,255,0.18),\n        stop: 0.491 rgba(255,255,255,0.8),\n        stop: 0.51 rgba(255,255,255,0.8),\n        stop: 0.511 rgba(255,255,255,0.18),\n        stop: 0.56 rgba(255,255,255,0.18),\n        stop: 0.561 rgba(255,255,255,0.8),\n        stop: 0.58 rgba(255,255,255,0.8),\n        stop: 0.581 rgba(255,255,255,0.18),\n        stop: 1 rgba(255,255,255,0.18)\n    );\n}\n\nQScrollBar::add-line:vertical,\nQScrollBar::sub-line:vertical {\n    height: 0px;\n}\n\n/* Inputs */\nQLineEdit, QComboBox {\n    background-color: #12191F;\n    border: 1px solid #34424D;\n    border-radius: 8px;\n    padding: 8px 10px;\n}\nQLineEdit:focus, QComboBox:focus {\n    border: 1px solid #4C89FF;\n}\n\nQPushButton {\n    background-color: #2B3A46;\n    border: 1px solid #3C4D5B;\n    border-radius: 8px;\n    padding: 8px 10px;\n    font-weight: 600;\n}\nQPushButton:hover {\n    background-color: #324454;\n}\nQPushButton:pressed {\n    background-color: #2A3944;\n}\nQPushButton:disabled {\n    background-color: #24303A;\n    color: #8E99A3;\n    border-color: #2C3A45;\n}\n\n/* Task card */\n.TaskCard {\n    background-color: #12191F;\n    border: 1px solid #2B3A46;\n    border-radius: 10px;\n}\n.TaskTitle {\n    font-size: 13px;\n    font-weight: 700;\n    color: #EAEAEA;\n}\n.TaskMeta {\n    font-size: 11px;\n    color: #EAEAEA;\n}\n.TaskMetaDone {\n    font-size: 11px;\n    color: #7C8791;\n}\n\"\"\"\n